import React, { Component } from "react";

import { Title } from "./components/Title";
import { SearchForm } from "./components/SearchForm";

import "./App.css";
import "bulma/css/bulma.css";

class App extends Component {
  state = {
    resultados: []
  };

  __handleResults = result => {
    this.setState({
      resultados: result
    });
  };

  _renderResults() {
    const result = this.state.resultados;
    return result.map(pelicula => {
      return <p key={pelicula.imdbID}>{pelicula.Title}</p>;
    });
  }

  render() {
    return (
      <div className="App">
        <Title>Buscador de peliculas</Title>
        <div className="SearchForm-wrapper">
          <SearchForm onResults={this.__handleResults} />
        </div>
        {this.state.resultados.length === 0 ? (
          <p>Sin resultaodos</p>
        ) : (
          this._renderResults()
        )}
      </div>
    );
  }
}

export default App;
