import React, { Component } from "react";

import { Title } from "./components/Title";
import { SearchForm } from "./components/SearchForm";
import { Movie } from "./components/Movis";

import "./App.css";
import "bulma/css/bulma.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      result: []
    };
  }

  resultados = result => {
    this.setState({
      resultados: result
    });
  };

  listar = e => {
    const result = this.state.resultados;
    return result.map(pelicula => {
      return (
        <Movie
          key={pelicula.imdbID}
          title={pelicula.Title}
          poster={pelicula.Poster}
          year={pelicula.Year}
        />
      );
    });
  };

  render() {
    return (
      <div className="App">
        <Title>Buscador de peliculas</Title>
        <div className="SearchForm-wrapper">
          <SearchForm onResults={this.resultados} />
        </div>
        {this.state.resultados.length === 0 ? (
          <p>Sin resultaodos</p>
        ) : (
          this.listar()
        )}
      </div>
    );
  }
}

export default App;
